property.code:
require(['jquery', 'urlshortener-localization'], function ($, l10n) {
        function regenerateURL(element) {
        element = element.currentTarget;
        const oldPageID = element.getAttribute('data-old-page-id');
        const pageRef = element.getAttribute('data-page-ref');
        const postURL = '/xwiki/rest/p/regenerate?' + new URLSearchParams({ oldPageID: oldPageID, currentDocRef: pageRef });
        let notification = new XWiki.widgets.Notification(l10n.regenerateInProgress,'inprogress');
        $.post(postURL, {}).done(function(response) {
        notification.replace(new XWiki.widgets.Notification(l10n.regenerateDone,'done'));
        $(element).prop('disabled', 'true');
        }).fail(function() {
        notification.replace(new XWiki.widgets.Notification(l10n.regenerateFail,'error'));
        });
        };
        $('.urlshortener-regenerate-btn').click(regenerateURL);
        });

define('urlshortener-translation-keys', {
        prefix: 'urlshortener.duplicates.',
        keys: [
        'regenerate.inprogress',
        'regenerate.done',
        'regenerate.fail',
        ],
        });

        define('urlshortener-localization', ['xwiki-l10n!urlshortener-translation-keys'], function (l10n) {
        return {
        regenerateInProgress: l10n['regenerate.inprogress'],
        regenerateDone: l10n['regenerate.done'],
        regenerateFail: l10n['regenerate.fail'],
        };
        });
